// pages/index/workDetail.js
var app = getApp()
Page({

  /**
   * 页面的初始数据
   */
  data: {
    isTeacher: app.globalData.isTeacher,
    workSummaryList: app.globalData.workSummaryList,
    workSummary: {},
    workPictrueList: {},
    //rate star
    starIndex: 0,//星星数量
    onScreen: false,
    studentList: [],
    studentListActive: [],
  },

  queryPictures() {
    let self = this
    //picture list
    let pictureList = {
      1: ['data:image/png;base64,', 'data:image/png;base64,'],
      2: ['data:image/png;base64,', 'data:image/png;base64,', ''],
      3: ['data:image/png;base64,', 'data:image/png;base64,', '', '']
    }
    self.setData({
      workPictrueList: pictureList
    })
  },

  PreviewImage(e) {
    var self = this
    var current = e.target.dataset.src;
    wx.previewImage({
      current: current, // 当前显示图片的http链接
      urls: self.data.workPictrueList[self.data.workSummary.workId] // 需要预览的图片http链接列表
    })
  },

  onChange(e) {
    const index = e.detail.index;
    this.setData({
      'starIndex': index
    })
  },

  screenStudents(e) {
    let self = this
    self.setData({
      onScreen: !self.data.onScreen
    })
  },

  //请求学生列表
  queryStudentList(e) {
    console.log('workId = ',e)
    let self = this

    ///请求学生列表样式
    let studentList = [
      ['王一一', true],
      ['邓二二', false],
      ['欧欧三三', true],
      ['四', false],
      ['五', true],
      ['六', false],
      ['七', true],
      ['八', false],
      ['九', true],
      ['十', false],
    ]
    ///////////////////

    
    let studentListActive = []
    for (var i = 0; i < studentList.length; i++)
      if (studentList[i][1] == true)
        studentListActive.push(studentList[i])
    self.setData({
      studentList: studentList,
      studentListActive: studentListActive
    })
  },

  //请求星星数量
  queryStarIndex(e){
    console.log('workId = ',e)
    let self = this

    ///请求星星数量样式
    let starIndex = 3
    ///////////////////

    self.setData({
      starIndex: starIndex
    })
  },

  submitRate(e) {
    console.log('submit')
  },

  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {
    let self = this
    //get workId
    let num = JSON.parse(options.current)
    //get workSummaryList[workId]
    var workSummaryList = this.data.workSummaryList
    let i = 0
    for (; i < workSummaryList.length; i++)
      if (workSummaryList[i].workId == num)
        break
    self.setData({
      workSummary: workSummaryList[i]
    })
    self.queryPictures()
    let teacherName = self.data.workSummary.teacherName + '老师'
    //set title
    wx.setNavigationBarTitle({
      title: teacherName
    })
    if (self.data.isTeacher == true) {
      console.log('teacher')
      self.queryStudentList(num)
      self.queryStarIndex(num)
    }
    else{
      console.log('partent')
      self.queryStarIndex(num)
    }
  },

  /**
   * 生命周期函数--监听页面初次渲染完成
   */
  onReady: function () {

  },

  /**
   * 生命周期函数--监听页面显示
   */
  onShow: function () {

  },

  /**
   * 生命周期函数--监听页面隐藏
   */
  onHide: function () {

  },

  /**
   * 生命周期函数--监听页面卸载
   */
  onUnload: function () {

  },

  /**
   * 页面相关事件处理函数--监听用户下拉动作
   */
  onPullDownRefresh: function () {

  },

  /**
   * 页面上拉触底事件的处理函数
   */
  onReachBottom: function () {

  },

  /**
   * 用户点击右上角分享
   */
  onShareAppMessage: function () {

  }
})